mapper.select:
SELECT p.ID as id, p.NAME as name, p.DESCRIPTION as description, p.LECTURER_ID as lecturerId, level.LEVEL_NAME as levelName FROM project as p, level
p.DELETE_FLG ='0' AND p.LECTURER_ID = #{lecturerId} AND LEVEL_ID = p.LEVEL
ORDER BY FACULTY_ID ASC
ORDER BY ${orderBy} ${orderType}
LIMIT #{offset}, #{limit}
